import{_ as t}from"./Row.vue_vue_type_script_setup_true_lang-dcfd8c7c.js";import{d as s,c as n,w as l,o,a as i,u as a}from"./index-6266c087.js";const r="/assets/belarus1-ea2d30c2.jpg",e="/assets/belarus2-f533ddbf.jpg",h=i("h1",{class:"w-[1230px] text-slate-600 text-center text-2xl my-4"}," Belarus Respublikasi Prezidenti Aleksandr Lukashenkoning O’zbekistonga tashrifi ",-1),u=i("p",{class:"w-[1230px] text-slate-600 text-justify text-xl"}," Belarus Respublikasi Prezidenti Aleksandr Lukashenkoning 2018 yil 12-14 sentabr kunlari O‘zbekiston Respublikasiga qilgan davlat tashrifidan so‘ng Belarus Respublikasining bir qator oliy ta'lim muassasalari bilan respublikamizning yetakchi oliy ta'lim muassasalari o‘rtasida hamkorlik yo‘lga qo‘yildi. ",-1),d=["src"],m=i("h1",{class:"w-[1230px] text-slate-600 text-center text-2xl my-4"}," O‘zbekiston Respublikasi Prezidentining 20.11.2019 yildagi PQ-4524-sonli qaroriga muvofiq: ",-1),y=i("p",{class:"w-[1230px] text-slate-600 text-justify text-xl"}," Respublika iqtisodiyoti tarmoqlari uchun yuqori malakali muhandis-texnik kadrlar tayyorlashni yanada takomillashtirish, oliy ta’lim sohasida xalqaro hamkorlikni kengaytirish hamda 2017 — 2021-yillarda O‘zbekiston Respublikasini rivojlantirishning beshta ustuvor yo‘nalishi bo‘yicha Harakatlar strategiyasiga muvofiq Belarus Respublikasi Ta’lim vazirligi, O‘zbekiston Respublikasi Oliy ta’lim , fan va innovatsiyalar vazirligi, Belarus milliy texnika universiteti, Respublika kasb-hunar ta’limi instituti (Belarus Respublikasi) va Islom Karimov nomidagi Toshkent davlat texnika universitetining Toshkent shahrida Belarus-O‘zbekiston qo‘shma tarmoqlararo amaliy texnik kvalifikatsiyalar institutini (keyingi o‘rinlarda — Institut) tashkil etish to‘g‘risidagi taklifiga rozilik berildi. ",-1),x=i("h1",{class:"w-[1230px] text-black text-justify text-2xl my-4"}," Undan tashqari Oliy ta'lim muassasalarining rektorlar Forumi bo’lib o’tdi va unda S.M.Turabjonov ham qatnashdilar. ",-1),g=["src"],k=i("p",{class:"w-[1230px] text-slate-600 text-justify text-xl"}," Belarus Respublikasining Minsk shahrida O'zbekiston va Belarus oliy ta'lim muassasalarining rektorlar ajnumani bo'lib o'tdi. ",-1),v=i("p",{class:"w-[1230px] text-slate-600 text-justify text-xl"}," Anjuman davomida ikki mamlakatning 30 dan ortiq universitet rektorlari oliy ta'lim tizimini rivojlantirish masalalarini muhokama qildilar. ",-1),b=i("p",{class:"w-[1230px] text-slate-600 text-justify text-xl"}," Anjuman yakunida Toshkent davlat texnika universiteti Belarusning Belarus davlat informatika va radio qurilmalar universiteti, Belarus milliy texnika universiteti va Belarus davlat iqtisodiyot universitetlari bilan hamkorlik shartnomalarini imzoladi. ",-1),c=i("p",{class:"w-[1230px] text-slate-600 text-justify text-xl"}," O‘zbekistonda innovatsion iqtisodiyotni rivojlantirishning dolzarb yo‘nalishi sifatida biz quyidagi sabablarga ko‘ra texnologiyalar transferini ko‘rib chiqishni taklif qilamiz: ",-1),q=i("li",{class:"w-[1230px] text-slate-600 text-justify text-xl"}," Texnologiyalar transferining iqtisodiy mazmuni va mohiyati butun davlatning innovatsion rivojlanishiga vositachilik qiluvchi texnologiya harakatining doimiy jarayonini nazarda tutadi; ",-1),p=i("li",{class:"w-[1230px] text-slate-600 text-justify text-xl"}," O‘zbekistonda zamonaviy texnologiyalar transferi yordamida innovatsion infratuzilmaning intellektual tashkilotlar, virtual korporatsiyalar, bozor-intellektual korxonalar, texnologiyalar transferi markazlari, innovatsion konsalting va boshqalar kabi elementlari rivojlantiriladi va takomillashtiriladi; ",-1),f=i("li",{class:"w-[1230px] text-slate-600 text-justify text-xl"}," texnologiyalar transferi mamlakatni modernizatsiya va innovatsiyalar doirasida milliy innovatsion siyosatni amalga oshirishning samarali vositasi bo‘lib xizmat qilishi mumkin; ",-1),_=i("li",{class:"w-[1230px] text-slate-600 text-justify text-xl"}," Samarali texnologiyalar transferini yaratish va rivojlantirish davlatning jahon miqyosidagi mavqeini mustahkamlaydi va xalqaro hamkorlik va hamkorlikni rivojlantirishga yordam beradi. ",-1),j=i("p",{class:"w-[1230px] text-slate-600 text-justify text-xl"}," Fan va texnika sohasidagi yutuqlardan foydalanishning asosiy yo‘nalishi xalqaro ilmiy-texnika almashinuvidir. Uni amalga oshirish va ishlab chiqish O‘zbekiston va Belarusda bozor o‘zgarishlari va iqtisodiy o‘sish yutuqlarini amalga oshirishning muhim shartidir. ",-1),z=i("p",{class:"w-[1230px] text-slate-600 text-justify text-xl"}," Ilmiy-texnikaviy mahsulotlar bozori turli shakllarda keladi. Ilmiy-texnik mahsulotlar bozorining mavjud bo'lish shakllari patentlar, litsenziyalar, shartnomalar, nou-xau va yuqori texnologiyali mahsulotlarni o'tkazish bo'yicha bozorlardir. O'z ob'ektlariga bo'lgan intellektual mulk huquqlari bozori ushbu mulkka bo'lgan huquqlar sifatida ilmiy va texnologik mahsulotlar bozoridan ajralmasdir. Ular chuqur dialektik aloqada bo'lib, uning umumiy integral xususiyatini ta'minlaydi. ",-1),w=i("p",{class:"w-[1230px] text-slate-600 text-justify text-xl"}," Turli kontseptsiyalarni tahlil qilish shuni ko'rsatadiki, universitetlarning innovatsion faoliyati tabiiy ravishda universitet innovatsion makonining kengayishi bilan uyg'unlashadi, bu esa oliy ta'limning yuqori darajadagi innovatsion tizimga keyingi integratsiyalashuvidan oldin keladi. Bu, birinchi navbatda, milliy innovatsion tizim tarkibiga, keyin esa bevosita global innovatsion makonga kiritishdir. Respublikada innovatsion rivojlanishni jadallashtirish, iqtisodiyotning barcha tarmoqlariga innovatsiyalar va texnologiyalarni keng joriy etish, inson kapitalini, ilmiy va innovatsion yo‘nalishlarni rivojlantirish maqsadida 2022-2026-yillarda O‘zbekiston Respublikasini innovatsion rivojlantirish strategiyasi” 2022-yil 6-iyuldagi PF-165-son va O‘zbekiston Respublikasi Prezidentining “Innovatsion rivojlanish strategiyasini amalga oshirish bo‘yicha tashkiliy chora-tadbirlar to‘g‘risida”gi qarori qabul qilindi. O‘zbekiston Respublikasining 2022-2026 yillarga mo‘ljallangan”. Farmon va qaror innovatsion faoliyatni rivojlantirishga, jumladan, bilim va texnologiyalarni uzatish va ilmiy-tadqiqot va ilmiy-tadqiqot ishlari natijalarini tijoratlashtirishga qaratilgan boʻlib, ular ikki turdagi muassasalar orqali amalga oshiriladi: baʼzilari texnologiyalarni ishlab chiqish va joriy etishda biznesning talabiga binoan tashkil etiladi; boshqalari ma'lum bozor segmentida yoki iqtisodiy sohada transferlarni amalga oshirish uchun innovatsion jarayon ishtirokchilarining hamkorligini ta'minlaydi. ",-1),B=i("p",{class:"w-[1230px] text-slate-600 text-justify text-xl"}," Tahlil shuni ko'rsatdiki, zamonaviy universitetlar nafaqat tarmoq o'zaro aloqalariga integratsiyalashgan, balki yangi universitet modeli - tarmoq universitetini ham yaratadi. Bugungi kunda MDH Tarmoq universiteti va ShHT Tarmoq universiteti tashkil etilgan bo‘lib, boshqa tarmoq universitetlarini yaratish bo‘yicha takliflar ishlab chiqilmoqda. Masalan, MDH Tarmoq universiteti (tayanch universitet - Rossiya Xalqlar doʻstligi universiteti) MDHga aʼzo davlatlar talabalarining bir qator magistratura yoʻnalishlari boʻyicha akademik harakatchanligini rivojlantirishga qaratilgan. ",-1),R=i("p",{class:"w-[1230px] text-slate-600 text-justify text-xl"}," Toshkent davlat texnika universitetining www.science-tstu.uz rasmiy sayti ishlab chiqildi. Ilmiy ish va innovatsiyalarni qo'llab-quvvatlash va targ'ib qilish uchun sayt. Ushbu sayt - “Raqamli transformatsiya texnik universiteti uchun innovatsion muhitni loyiha sifatida rivojlantirish modelini ishlab chiqish (Belarus Milliy texnika universiteti va Islom Karimov nomidagi Toshkent davlat texnika universiteti misolida)” manba hisoblanadi. universitetning ilmiy faoliyati to'g'risidagi ma'lumotlar. ",-1),T=i("p",{class:"w-[1230px] text-slate-600 text-justify text-xl"}," Toshkent davlat texnika universitetining innovatsion muhitini raqamlashtirish bo‘yicha quyidagi tavsiyalar taklif etiladi: - universitet ichidagi elektron hujjat aylanishi tizimi (mavjud hujjat aylanishi har bir bosqichda, qabul boshlanishidan to yakunlashgacha raqamlashtirilishi kerak bo'lgan ishlarning kichik qismini qamrab oladi) va uni umumiy raqamli aloqa tizimiga bog'lash; ",-1),O=i("p",{class:"w-[1230px] text-slate-600 text-justify text-xl"}," - elektron qabul komissiyasi ",-1),I=i("p",{class:"w-[1230px] text-slate-600 text-justify text-xl"}," - o'quv binolari va yotoqxonalarda bepul simsiz internet ",-1),U=i("p",{class:"w-[1230px] text-slate-600 text-justify text-xl"}," - sport seksiyalarida elektron ro'yxatdan o'tish ",-1),M=i("p",{class:"w-[1230px] text-slate-600 text-justify text-xl"}," - universitet televideniyesi ",-1),P=i("p",{class:"w-[1230px] text-slate-600 text-justify text-xl"}," - raqamli aloqa markazi ",-1),D=i("p",{class:"w-[1230px] text-slate-600 text-justify text-xl"}," - universitetning marketing bo'limi va universitet bitiruvchilari iste'molchilari o'rtasidagi aloqani raqamli transformatsiya qilish ",-1),H=i("p",{class:"w-[1230px] text-slate-600 text-justify text-xl"}," - muhandislik-texnologiya korxonalari muammolari bo'yicha ma'lumotlar bazasini yaratish, bu muammolarni hal qilish usullarini o'zgartirishni hisobga olgan holda ",-1),A=i("p",{class:"w-[1230px] text-slate-600 text-justify text-xl"}," - universitetning o‘zida samarali boshqaruv va bozorni o‘rganuvchi hamda mahsulot ishlab chiqarishni ta’minlash uchun universitetning innovatsion ishlanmalari bazasidan foydalangan holda texnologik parkni bosqichma-bosqich tashkil etish ",-1),F=i("p",{class:"w-[1230px] text-slate-600 text-justify text-xl"}," - transformatsiyalar orqali mahsulot sotishni rivojlantirish va o'zaro ta'sir qilish uchun universitetlar tarmog'ini ta'minlash ",-1),N=i("p",{class:"w-[1230px] text-slate-600 text-justify text-xl"}," Universitetning innovatsion jarayonining modeli ishlab chiqildi va taklif qilindi, unda universitetning ilmiy-texnikaviy ishlanmalari oqimi ketma-ket o'tib, sifat jihatidan yangi holatga ega bo'ladi, ya'ni ilmiy tadqiqot ishlari (ITI) tajriba-konstruktorlik ishlariga aylantiriladi (R&D), ular o'z navbatida eksperimental, so'ngra prototiplar yoki texnologiyalarni ishlab chiqarish bilan almashtiriladi, ular bir qator tadbirlar amalga oshirilganda innovatsiyaga aylanadi, ya'ni ular yangi sifatga ega bo'ladi - ular tovarga aylanadi. ",-1),S=i("p",{class:"w-[1230px] text-slate-600 text-justify text-xl"}," Raqamli iqtisodiyotda BNTUning ilmiy va innovatsion faoliyati strategiyasining tahlili shuni ko'rsatdiki, universitet nafaqat tadbirkorlar tomonidan buyurtma qilingan ilmiy tadqiqot va ishlanmalarni amalga oshirish uchun shart-sharoitlarni ta'minlaydi, balki talabga javob beradigan, yangi mahsulotlar, texnologiyalarni yaratadigan innovatsion biznesni yaratadi. , va bozorlar. Universitet innovatsion muhitining ushbu komponentining asosiy elementi universitet negizida innovatsion korxonalar, texnologiyalar transferi markazlari, biznes inkubatorlar va texnoparklar tashkil etish bo‘lishi kerak ",-1),C=i("p",{class:"w-[1230px] text-slate-600 text-justify text-xl"}," Raqamli iqtisodiyotda BNTUning ilmiy va innovatsion faoliyati strategiyasining tahlili shuni ko'rsatdiki, universitet nafaqat tadbirkorlar tomonidan buyurtma qilingan ilmiy tadqiqot va ishlanmalarni amalga oshirish uchun shart-sharoitlarni ta'minlaydi, balki talabga javob beradigan, yangi mahsulotlar, texnologiyalarni yaratadigan innovatsion biznesni yaratadi. , va bozorlar. Universitet innovatsion muhitining ushbu komponentining asosiy elementi universitet negizida innovatsion korxonalar, texnologiyalar transferi markazlari, biznes inkubatorlar va texnoparklar tashkil etish bo‘lishi kerak ",-1),K=i("p",{class:"w-[1230px] text-slate-600 text-justify text-xl"}," Universitetlarning innovatsion muhitini rivojlantirishni baholash uchun innovatsion muhitni rivojlantirishning integral indeksi (IRIS) hisoblab chiqilgan. Tahlil uchun BNTU va TDTUning 2021 yildagi ilmiy va innovatsion faoliyati toʻgʻrisidagi hisobot maʼlumotlaridan foydalanildi. O‘zbekiston Respublikasi rahbarining qarori tahlili va amalga oshirilishini inobatga olgan holda, mazkur loyiha doirasida Toshkent davlat texnika universitetining raqamli iqtisodiyotda ilmiy-innovatsion faoliyatini yo‘lga qo‘yish strategiyasi taklif etildi. ",-1),E=s({__name:"InnoInfo",setup(L){return(Q,V)=>(o(),n(t,{styles:"bg-white min-h-[100vh] justify-start",label:""},{default:l(()=>[h,u,i("img",{class:"w-[1230px] rounded-md border-2 border-slate-600 h-[600px]",src:a(r),alt:"img"},null,8,d),m,y,x,i("img",{class:"w-[1230px] rounded-md border-2 border-slate-600 h-[600px]",src:a(e),alt:"img"},null,8,g),k,v,b,c,q,p,f,_,j,z,w,B,R,T,O,I,U,M,P,D,H,A,F,N,S,C,K]),_:1}))}});export{E as default};
